import loggingimport osfrom aiogram import Bot, Dispatcher, typesfrom aiogram.utils import executorfrom dotenv import load_dotenv# 注 砖转 (砖 拽)load_dotenv()# 拽转 拽API_TOKEN = os.getenv('TELEGRAM_API_TOKEN')# 专转  -  砖专  拽专logging.basicConfig(level=logging.INFO)if not API_TOKEN:    print("CRITICAL ERROR: Token is missing!")bot = Bot(token=API_TOKEN)dp = Dispatcher(bot)@dp.message_handler(commands=['start'])async def send_welcome(message: types.Message):    await message.reply("!   ! \n( 专住转 拽)")if __name__ == '__main__':    print("Bot is starting...")    # 专爪 驻砖  砖专转 住    executor.start_polling(dp, skip_updates=True)